jQuery(document).ready(function($) {
  if ($(window).width() > 768) {
    var containerMargin = $('.container').css('marginLeft');
    $('.main--info').css('marginRight', containerMargin);
    $('.main--search').css('paddingLeft', containerMargin);
    $('.main--title').css('paddingLeft', containerMargin);
    $(window).on('resize', function(){
      containerMargin = $('.container').css('marginLeft');
      $('.main--info').css('marginRight', containerMargin);
      $('.main--search').css('paddingLeft', containerMargin);
      $('.main--title').css('paddingLeft', containerMargin);
    })
  }
  $('.menu-icon').click(function(event) {
    $('.nav').toggleClass('active');
  });
});